package practice1;

public class EqualsTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Student studentLee = new Student(100,"이상원");
		Student studentLee2 = studentLee;
		Student studentSang = new Student(100,"이상원");
		
		System.out.println("studentLee 와 studentLee2");
		if(studentLee == studentLee2) {
			System.out.println("studentLee와 studentLee2의  주소는 같다");
		}
		else {
			System.out.println("studentLee와 studentLee2의  주소는 같지 않다");
		}
		if(studentLee.equals(studentLee2)) {
			System.out.println("studentLee와 studentLee2는 동일하다");
		}
		else {
			System.out.println("studentLee와 studentLee2는 동일하지않다");
		}
		
		System.out.println("studentLee 와 studentSang");
		if(studentLee == studentSang) {
			System.out.println("studentLee와 studentSang의  주소는 같다");
		}
		else {
			System.out.println("studentLee와 studentSang의  주소는 같지 않다");
		}
		if(studentLee.equals(studentSang)) {
			System.out.println("studentLee와 studentSang는 동일하다");
		}
		else {
			System.out.println("studentLee와 studentSang는 동일하지않다");
		}
		//인스턴스 생성시 만들어지는 heap 메모리주소
		System.out.println("studentLee의 hashcode 값 : "+ studentLee.hashCode());
		System.out.println("studentSang의 hashcode 값 : "+ studentSang.hashCode());
		//인스턴스 전에 주어지는 실제 주소
		System.out.println("studentLee의 실제주소 값 : "+ System.identityHashCode(studentLee));
		System.out.println("studentSang의 실제주소 값 : "+ System.identityHashCode(studentSang));
	
	}

}
